// Testimonials Section Component
const TestimonialsSection = ({ onStartTrial }) => {
  const [active, setActive] = useState(0)
  const [isPaused, setIsPaused] = useState(false)
  const [selectedPlan, setSelectedPlan] = useState('pro')

  const plans = [
    {
      id: 'starter',
      name: 'Starter',
      price: '$199',
      period: '/week',
      desc: 'Perfect for individuals',
      features: ['Up to 50 testimonials', 'Basic analytics', 'Email support', 'Widget embeds'],
      color: '#64748b',
      badge: null,
    },
    {
      id: 'pro',
      name: 'Pro',
      price: '$499',
      period: '/month',
      desc: 'Most popular for teams',
      features: ['Unlimited testimonials', 'Advanced analytics', 'Priority support', 'Custom branding', 'API access'],
      color: '#00e5ff',
      badge: 'Most Popular',
    },
    {
      id: 'business',
      name: 'Business',
      price: '$1299',
      period: '/year',
      desc: 'For growing agencies',
      features: ['Everything in Pro', 'Multi-site support', 'Dedicated account manager', 'White-label option', 'SLA guarantee'],
      color: '#7c3aed',
      badge: 'Best Value',
    },
  ]

  const prev = () => setActive(i => (i - 1 + testimonials.length) % testimonials.length)
  const next = () => setActive(i => (i + 1) % testimonials.length)

  // Auto-rotate every 4s unless hovered
  useEffect(() => {
    const interval = setInterval(() => {
      if (!isPaused) setActive(i => (i + 1) % testimonials.length)
    }, 4000)
    return () => clearInterval(interval)
  }, [isPaused])

  const t = testimonials[active]

  return (
    <section className="testimonials" id="testimonials">
      <div className="container">
        <div className="section-header">
          <span className="section-tag">What People Say</span>
          <h2 className="section-title">Trusted by Our Community</h2>
          <p className="section-description">Real experiences from real users and business owners</p>
        </div>
        

        <div
          className="testimonial-wrapper"
          onMouseEnter={() => setIsPaused(true)}
          onMouseLeave={() => setIsPaused(false)}
        >
          {/* Side cards */}
          <div className="testimonial-track">
            {testimonials.map((item, index) => {
              const offset = (index - active + testimonials.length) % testimonials.length
              const isActive = offset === 0
              const isPrev = offset === testimonials.length - 1
              const isNext = offset === 1
              const isHidden = !isActive && !isPrev && !isNext

              return (
                <div
                  key={item.id}
                  className={`testimonial-card ${isActive ? 'active' : ''} ${isPrev ? 'prev' : ''} ${isNext ? 'next' : ''} ${isHidden ? 'hidden-card' : ''}`}
                  onClick={() => setActive(index)}
                >
                  <div className="testimonial-quote">"</div>
                  <p className="testimonial-text">{item.text}</p>
                  <div className="testimonial-stars">
                    {Array.from({ length: item.rating }).map((_, i) => (
                      <span key={i} className="testimonial-star">‚òÖ</span>
                    ))}
                  </div>
                  <div className="testimonial-author">
                    <div className="testimonial-avatar">{item.avatar}</div>
                    <div className="testimonial-info">
                      <span className="testimonial-name">{item.name}</span>
                      <span className="testimonial-role">{item.role}</span>
                    </div>
                  </div>
                </div>
              )
            })}
          </div>

          {/* Navigation */}
          <div className="testimonial-nav">
            <button className="testimonial-btn" onClick={prev} aria-label="Previous">
              <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2.5">
                <polyline points="15 18 9 12 15 6"/>
              </svg>
            </button>
            <div className="testimonial-dots">
              {testimonials.map((_, i) => (
                <button
                  key={i}
                  className={`testimonial-dot ${i === active ? 'active' : ''}`}
                  onClick={() => setActive(i)}
                  aria-label={`Go to testimonial ${i + 1}`}
                />
              ))}
            </div>
            <button className="testimonial-btn" onClick={next} aria-label="Next">
              <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2.5">
                <polyline points="9 18 15 12 9 6"/>
              </svg>
            </button>
          </div>
        </div>

        {/* Pricing Packages */}
        <div style={{ marginTop: '64px' }}>
          <div className="section-header" style={{ marginBottom: '32px' }}>
            <span className="section-tag">Pricing</span>
            <h2 className="section-title">Choose Your Plan</h2>
            <p className="section-description">Simple, transparent pricing for every stage of growth</p>
          </div>
          <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(220px, 1fr))', gap: '20px', marginBottom: '32px' }}>
            {plans.map(plan => (
              <div
                key={plan.id}
                onClick={() => setSelectedPlan(plan.id)}
                style={{
                  background: '#fff',
                  border: `2px solid ${selectedPlan === plan.id ? plan.color : '#e2e8f0'}`,
                  borderRadius: '16px',
                  padding: '28px 24px',
                  cursor: 'pointer',
                  position: 'relative',
                  transition: 'border-color 0.2s, box-shadow 0.2s, transform 0.2s',
                  boxShadow: selectedPlan === plan.id ? `0 0 0 3px ${plan.color}22, 0 8px 32px rgba(0,0,0,0.08)` : '0 2px 8px rgba(0,0,0,0.04)',
                  transform: selectedPlan === plan.id ? 'translateY(-4px)' : 'none',
                }}
              >
                {plan.badge && (
                  <span style={{
                    position: 'absolute', top: -13, left: '50%', transform: 'translateX(-50%)',
                    background: plan.color, color: plan.id === 'pro' ? '#06061a' : '#fff',
                    fontSize: 11, fontWeight: 700, borderRadius: 20, padding: '3px 12px',
                    whiteSpace: 'nowrap', letterSpacing: '0.04em',
                  }}>{plan.badge}</span>
                )}
                <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', marginBottom: '6px' }}>
                  <span style={{ fontWeight: 700, fontSize: 17, color: '#1e293b' }}>{plan.name}</span>
                  <span style={{
                    width: 20, height: 20, borderRadius: '50%',
                    border: `2px solid ${selectedPlan === plan.id ? plan.color : '#cbd5e1'}`,
                    background: selectedPlan === plan.id ? plan.color : 'transparent',
                    display: 'inline-block', flexShrink: 0,
                  }} />
                </div>
                <div style={{ fontSize: 28, fontWeight: 800, color: plan.color, lineHeight: 1.1, marginBottom: 2 }}>
                  {plan.price}<span style={{ fontSize: 14, fontWeight: 500, color: '#64748b' }}>{plan.period}</span>
                </div>
                <p style={{ fontSize: 13, color: '#64748b', margin: '6px 0 16px' }}>{plan.desc}</p>
                <ul style={{ listStyle: 'none', margin: 0, padding: 0, display: 'flex', flexDirection: 'column', gap: 7 }}>
                  {plan.features.map(f => (
                    <li key={f} style={{ fontSize: 13, color: '#475569', display: 'flex', alignItems: 'center', gap: 7 }}>
                      <span style={{ color: plan.color, fontWeight: 700 }}>‚úì</span> {f}
                    </li>
                  ))}
                </ul>
              </div>
            ))}
          </div>
          <div style={{ textAlign: 'center' }}>
            <button
              className="btn btn-primary btn-lg"
              onClick={() => onStartTrial && onStartTrial()}
              style={{
                background: 'linear-gradient(135deg, #00e5ff, #0070f3)',
                border: 'none',
                padding: '14px 40px',
                fontSize: 15,
                fontWeight: 700,
                borderRadius: 10,
                cursor: 'pointer',
              }}
            >
              Start Free Trial ‚Äî {plans.find(p => p.id === selectedPlan)?.name} Plan
            </button>
            <p style={{ marginTop: 10, fontSize: 13, color: '#94a3b8' }}>2 days free ¬∑ No credit card required</p>
          </div>
        </div>
      </div>
    </section>
  )
}


